import { useState } from 'react';
import AppointmentCard from '../components/AppointmentCard';

export default function AppointmentsPage() {
  const [appointments, setAppointments] = useState([
    { id: 1, patient: 'John Doe', date: 'Nov 10, 2025', time: '10:00 AM', status: 'Confirmed' },
    { id: 2, patient: 'Jane Smith', date: 'Nov 11, 2025', time: '2:30 PM', status: 'Pending' },
    { id: 3, patient: 'Ali Mwangi', date: 'Nov 12, 2025', time: '9:15 AM', status: 'Completed' },
  ]);

  const cancelAppointment = (id) => {
    setAppointments(prev =>
      prev.map(appt =>
        appt.id === id ? { ...appt, status: 'Cancelled' } : appt
      )
    );
  };

  return (
    <div>
      <h2>Appointments</h2>
      {appointments.map(appt => (
        <AppointmentCard
          key={appt.id}
          patient={appt.patient}
          date={appt.date}
          time={appt.time}
          status={appt.status}
          onCancel={() => cancelAppointment(appt.id)}
        />
      ))}
    </div>
  );
}
