import { useState, useEffect } from 'react';
import AppointmentCard from '../components/AppointmentCard';
import NewAppointmentForm from '../components/NewAppointmentForm';

export default function AppointmentsPage() {
  const [appointments, setAppointments] = useState([]);
  const [filter, setFilter] = useState('All');

  useEffect(() => {
    const stored = localStorage.getItem('appointments');
    if (stored) {
      setAppointments(JSON.parse(stored));
    } else {
      setAppointments([
        { id: 1, patient: 'John Doe', date: '2025-11-10', time: '10:00', status: 'Confirmed' },
        { id: 2, patient: 'Jane Smith', date: '2025-11-11', time: '14:30', status: 'Pending' },
        { id: 3, patient: 'Ali Mwangi', date: '2025-11-12', time: '09:15', status: 'Completed' },
      ]);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('appointments', JSON.stringify(appointments));
  }, [appointments]);

  const addAppointment = (newAppt) => {
    const id = Date.now();
    setAppointments(prev => [...prev, { id, ...newAppt }]);
  };

  const cancelAppointment = (id) => {
    setAppointments(prev =>
      prev.map(appt =>
        appt.id === id ? { ...appt, status: 'Cancelled' } : appt
      )
    );
  };

  const filteredAppointments = filter === 'All'
    ? appointments
    : appointments.filter(appt => appt.status === filter);

  return (
    <div>
      <h2>Appointments</h2>
      <NewAppointmentForm onAdd={addAppointment} />

      <div style={{ marginBottom: '1rem' }}>
        <label>Filter by status: </label>
        <select value={filter} onChange={e => setFilter(e.target.value)}>
          <option>All</option>
          <option>Confirmed</option>
          <option>Pending</option>
          <option>Completed</option>
          <option>Cancelled</option>
        </select>
      </div>

      {filteredAppointments.map(appt => (
        <AppointmentCard
          key={appt.id}
          patient={appt.patient}
          date={appt.date}
          time={appt.time}
          status={appt.status}
          onCancel={() => cancelAppointment(appt.id)}
        />
      ))}
    </div>
  );
}
