import { useState } from 'react';
import AppointmentCard from '../components/AppointmentCard';
import NewAppointmentForm from '../components/NewAppointmentForm';

export default function AppointmentsPage() {
  const [appointments, setAppointments] = useState([
    { id: 1, patient: 'John Doe', date: '2025-11-10', time: '10:00', status: 'Confirmed' },
    { id: 2, patient: 'Jane Smith', date: '2025-11-11', time: '14:30', status: 'Pending' },
    { id: 3, patient: 'Ali Mwangi', date: '2025-11-12', time: '09:15', status: 'Completed' },
  ]);

  const addAppointment = (newAppt) => {
    const id = Date.now();
    setAppointments(prev => [...prev, { id, ...newAppt }]);
  };

  const cancelAppointment = (id) => {
    setAppointments(prev =>
      prev.map(appt =>
        appt.id === id ? { ...appt, status: 'Cancelled' } : appt
      )
    );
  };

  return (
    <div>
      <h2>Appointments</h2>
      <NewAppointmentForm onAdd={addAppointment} />
      {appointments.map(appt => (
        <AppointmentCard
          key={appt.id}
          patient={appt.patient}
          date={appt.date}
          time={appt.time}
          status={appt.status}
          onCancel={() => cancelAppointment(appt.id)}
        />
      ))}
    </div>
  );
}
